import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [stbData, setStbData] = useState(null);
  const [tags, setTags] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchCruiseData = async () => {
      try {
        const cruiseApiUrl =
          "https://api.stb.gov.sg/content/common/v2/search?dataset=accommodation";
        const headers = {
          Accept: "application/json",
          "X-Content-Language": "en",
          "X-API-Key": "2kuwN1gRBqECMuYTdxguaLry1U2QqH2R",
        };

        const cruiseResponse = await axios.get(cruiseApiUrl, { headers });
        setStbData(cruiseResponse.data);
      } catch (error) {
        console.error("Error fetching cruise data:", error);
      }
    };

    const fetchTags = async () => {
      try {
        const headers = {
          "X-Api-Key": "nE2LLxGGycJ7Egvtg2xXJZOpXNOVbKFW", // Replace with your actual API key
        };

        const tagsResponse = await axios.get("/content/common/v2/tags", {
          headers,
        });
        console.log("Tags API Response:", tagsResponse); // Log the response
        setTags(tagsResponse.data || []); // Adjust based on actual response structure
      } catch (error) {
        console.error("Error fetching tags:", error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchCruiseData();
    fetchTags();
  }, []);

  if (isLoading) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h1>Cruise Data</h1>
      {stbData && (
        <div>
          {stbData.data.map((cruise) => (
            <CruiseItem key={cruise.uuid} cruise={cruise} />
          ))}
        </div>
      )}

      <h1>Tags</h1>
      <div>
        {tags.length > 0 ? (
          tags.map((tag, index) => <p key={index}>{tag}</p>)
        ) : (
          <p>No tags found.</p>
        )}
      </div>
    </div>
  );
}

const CruiseItem = ({ cruise }) => {
  return (
    <div className="cruise-item">
      <h2>{cruise.name}</h2>
      <p>Type: {cruise.type}</p>
      <p>Description: {cruise.description}</p>
      <p>Rating: {cruise.rating}</p>
      {/* More details can be added here */}
    </div>
  );
};

export default App;
