import React, { useState, useEffect } from "react";
import axios from "axios";
import { BrowserRouter as Router, Route, Switch, Link } from "react-router-dom";
import AttractionDetails from "./AttractionDetails"; // Make sure you have this component

function App() {
  const [attractions, setAttractions] = useState([]);

  useEffect(() => {
    fetchAttractions();
  }, []);

  const fetchAttractions = async () => {
    try {
      const response = await axios.get("YOUR_ATTRACTIONS_API_ENDPOINT"); // Replace with your API endpoint
      setAttractions(response.data.data);
    } catch (error) {
      console.error("Error fetching attractions:", error);
    }
  };

  return (
    <Router>
      <Switch>
        <Route path="/" exact>
          <div>
            <h1>Attractions</h1>
            <div>
              {attractions.map((attraction) => (
                <AttractionItem key={attraction.uuid} attraction={attraction} />
              ))}
            </div>
          </div>
        </Route>
        <Route path="/attraction/:id" component={AttractionDetails} />
      </Switch>
    </Router>
  );
}

const AttractionItem = ({ attraction }) => {
  const imageUrl = attraction.thumbnails[0]?.url || "default-image-url"; // Replace 'default-image-url' with a placeholder image URL

  return (
    <div className="attraction-item">
      <img src={imageUrl} alt={attraction.name} />
      <Link to={`/attraction/${attraction.uuid}`}>{attraction.name}</Link>
      {/* Other details can be added here */}
    </div>
  );
};

export default App;
