import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [attractionTypes, setAttractionTypes] = useState([]);
  const [attractionData, setAttractionData] = useState(null);

  useEffect(() => {
    // Fetch attraction types
    const fetchAttractionTypes = async () => {
      try {
        const apiUrl =
          "https://api.stb.gov.sg/content/common/v2/types?category=attractions";
        const headers = {
          Accept: "application/json",
          "X-API-Key": "YOUR_API_KEY",
        };
        const response = await axios.get(apiUrl, { headers });
        setAttractionTypes(response.data.data);
      } catch (error) {
        console.error("Error fetching attraction types:", error);
      }
    };

    fetchAttractionTypes();
  }, []);

  const fetchAttractionData = async (type) => {
    try {
      const apiUrl = `API_ENDPOINT_FOR_ATTRACTION_DATA?type=${type}`; // Replace with the actual endpoint
      const headers = {
        Accept: "application/json",
        "X-API-Key": "YOUR_API_KEY",
      };

      const response = await axios.get(apiUrl, { headers });
      setAttractionData(response.data.data);
    } catch (error) {
      console.error("Error fetching data for attraction type:", error);
    }
  };

  return (
    <div>
      <h1>Attraction Types</h1>
      <ul>
        {attractionTypes.map((type, index) => (
          <li key={index} onClick={() => fetchAttractionData(type)}>
            {type}
          </li>
        ))}
      </ul>
      {attractionData && (
        <div>
          {/* Render the fetched data here */}
          <h2>Attraction Details</h2>
          {/* You can display the details of the attraction here */}
        </div>
      )}
    </div>
  );
}

export default App;
